import React  from "react"
import "./table.css"


const Table = (props) => <div className="main" id={props.title}>
    <h1>          
        <i className={props.icon} />
        {props.title} 
    </h1>
    <table>
        <tbody>
        {
            props.details.map((row, pos) => <>
            <tr>
                <td> {pos+1}       </td>
                <td> {(props.title==="Education")? row.Institute: row.Company}
                    <p className="date"> ({row.Date})  </p>
                </td>
                <td> {row.Designation} </td>
            </tr>
            <td> {row3(props.title, row)} </td>

        </>)
        }
        </tbody>
    </table>
</div>


const row3 = (title, row) => {
    if (title=== "Education") {
        return row.Percentage;
    } else if (title=== "Experience") {
        return <ul>
            {row.Projects.map(project => <li> {project} </li>)}
        </ul>
    }
}


export  default Table;
