import './App.css';
import React from 'react';
import 'font-awesome/css/font-awesome.min.css';

import {WorkTable, EducationTable}  from './components/table';
import {PersonalDetails}  from './components/personalDetails';


class App extends React.Component {

  constructor(props) {
    // initializing
    super(props);
    this.experience = require("./details/resume.json");
    this.personalDetails = reqire("./details/personal-details.json");
  }

  componentDidMount() {
    // Setting title of page
    document.title = "Resume";
    document.body.style.backgroundColor = "lightbrown";
  }



  render() {
    return (
      <div className="App" >
        <PersonalDetails  details={this.personalDetails} />
        <WorkTable      details={this.experience.Experience}     tableName="Experience" />
        <WorkTable      details={this.experience.Internship}     tableName="Internship" />
        <EducationTable details={this.experience.Education}      tableName="Education" />
    </div>
    );
  }
}



export default App;
