import React  from "react"
import "./table.css"



const showHideDetail = (divId) => {
    console.log(divId);
    if (document.getElementById(`${divId}-description`).style.display === "none") {
        document.getElementById(`${divId}-description`).style.display = null;
        document.getElementById(`${divId}-show-details`).innerHTML = "[hide details]"
    } else {
        document.getElementById(`${divId}-description`).style.display = "none";
        document.getElementById(`${divId}-show-details`).innerHTML = "[show details]"
    }
}

const addRow = (title, rows, header) => {
    const output = [];
    output.push(1)
    for (let pos=0; pos<=rows.length-1; pos++) {
        const row = rows[pos];
       // adding rows
       const val = <tr className="pointer-class" id={`${title}-${pos}`} onClick={showHideDetail(this.id)}>
            <td> {pos} </td>
            {header.map(val => <td> {row[val]} </td>)}
            <td className="show-details" id={`${title}-${pos}-show-details`}>
                [show details]
            </td>
        </tr>;
        output.push(val);

    } 
    return output;    
}



const Table = (props) => 
<div id={props.title}>
    <h1>          
        <i className={props.icon} />
        {props.title} 
    </h1>
    <table>
        <tr>
            <th> # </th>
            {props.header.map(val => <th> {val} </th>)}
            <th />
        </tr>
        {addRow(props.title, props.details, props.header)}
    </table>
</div>




export default Table;
